const tree = await vaultContract.getVirtualStepsTree(0);

console.log(
  tree.map((encodedStep) => {
    const res = [
      ...AbiCoder.defaultAbiCoder().decode([STEP_TUPLE], encodedStep)[0],
    ];

    const func =
      res[0] == ZeroHash
        ? null
        : [
            ...AbiCoder.defaultAbiCoder().decode(
              [FUNCTIONCALL_TUPLE],
              "0x" + res[0].slice(6)
            )[0],
          ];

    return {
      func: func ?  {
        targetAddr: func[0],
        sig: func[2],
        args: func[1],
      } : null,
      indices: res[1],
      isCallback: res[3],
      mvc: res[4],
    };
  })
);

